import { useState } from "react";
import TreatmentTemplate from "./TreatmentTemplate";

export default function TreatmentForm() {
  const [title, setTitle] = useState("");
  const [intro, setIntro] = useState("");
  const [procedure, setProcedure] = useState("");
  const [benefits, setBenefits] = useState("");
  const [cost, setCost] = useState("");
  const [faqs, setFaqs] = useState("");
  const [preview, setPreview] = useState(null);
  const [codeOutput, setCodeOutput] = useState("");
  const [outputFormat, setOutputFormat] = useState("react");

  const handlePreview = () => {
    const benefitsArray = benefits
      .split("\n")
      .map((b) => b.trim())
      .filter((b) => b);

    const faqsArray = faqs
      .split("\n---\n")
      .map((f) => {
        const [q, a] = f.split("\n");
        return { q: q?.trim() || "", a: a?.trim() || "" };
      })
      .filter((f) => f.q && f.a);

    const treatment = {
      title,
      intro,
      procedure,
      benefits: benefitsArray,
      cost,
      faqs: faqsArray,
    };

    setPreview(treatment);
    generateCode(treatment);
  };

  const generateCode = (treatment) => {
    if (outputFormat === "react") {
      generateReactCode(treatment);
    } else if (outputFormat === "html") {
      generateHTMLCode(treatment);
    }
  };

  const generateReactCode = (treatment) => {
    const benefitsString = treatment.benefits.map((b) => `"${b}"`).join(",\n    ");
    const faqsString = treatment.faqs.map((f) => `{ q: "${f.q}", a: "${f.a}" }`).join(",\n    ");

    const code = `export default function ${title.replace(/\s+/g, "")}() {
  const treatment = {
    title: "${treatment.title}",
    intro: "${treatment.intro}",
    procedure: "${treatment.procedure}",
    benefits: [
      ${benefitsString}
    ],
    cost: "${treatment.cost}",
    faqs: [
      ${faqsString}
    ],
  };

  return (
    <div style={{ padding: "20px", fontFamily: "Arial, sans-serif" }}>
      <h1 className="page-title">{treatment.title}</h1>

      <section style={{ marginBottom: "30px" }}>
        <h4 className="section-title">Overview</h4>
        <p className="text">{treatment.intro}</p>
      </section>

      <section style={{ marginBottom: "30px" }}>
        <h4 className="section-title">Procedure</h4>
        <p className="text">{treatment.procedure}</p>
      </section>

      <section style={{ marginBottom: "30px" }}>
        <h4 className="section-title">Benefits</h4>
        <ul className="list">
          {treatment.benefits.map((benefit, idx) => (
            <li key={idx} className="list-item">{benefit}</li>
          ))}
        </ul>
      </section>

      <section style={{ marginBottom: "30px" }}>
        <h4 className="section-title">Cost</h4>
        <p className="text">{treatment.cost}</p>
      </section>

      <section>
        <h4 className="section-title">FAQs</h4>
        {treatment.faqs.map((faq, idx) => (
          <div key={idx} style={{ marginBottom: "15px" }}>
            <p><strong>{faq.q}</strong></p>
            <p className="text">{faq.a}</p>
          </div>
        ))}
      </section>
    </div>
  );
}`;

    setCodeOutput(code);
  };

  const generateHTMLCode = (treatment) => {
    const benefitsHTML = treatment.benefits
      .map((b) => `            <li class="list-item">${b}</li>`)
      .join("\n");

    const faqsHTML = treatment.faqs
      .map(
        (f) => `        <div style="margin-bottom: 15px;">
          <p><strong>${f.q}</strong></p>
          <p class="text">${f.a}</p>
        </div>`
      )
      .join("\n");

    const code = `<!DOCTYPE html>
<html lang="en">
<head>
    <meta charset="UTF-8">
    <meta name="viewport" content="width=device-width, initial-scale=1.0">
    <title>${treatment.title}</title>
    <style>
        * {
            margin: 0;
            padding: 0;
            box-sizing: border-box;
        }
        body {
            font-family: Arial, sans-serif;
            background-color: #f9f9f9;
        }
        .container {
            max-width: 1200px;
            margin: 0 auto;
            padding: 20px;
        }
        .page-title {
            font-size: 32px;
            color: #333;
            margin-bottom: 20px;
        }
        .section-title {
            font-size: 20px;
            color: #555;
            margin-bottom: 15px;
            margin-top: 30px;
        }
        .text {
            font-size: 16px;
            color: #666;
            line-height: 1.6;
            margin-bottom: 10px;
        }
        .list {
            margin-left: 20px;
            margin-bottom: 10px;
        }
        .list-item {
            font-size: 16px;
            color: #666;
            line-height: 1.6;
            margin-bottom: 8px;
        }
        section {
            margin-bottom: 30px;
        }
    </style>
</head>
<body>
    <div class="container">
        <h1 class="page-title">${treatment.title}</h1>

        <section>
            <h4 class="section-title">Overview</h4>
            <p class="text">${treatment.intro}</p>
        </section>

        <section>
            <h4 class="section-title">Procedure</h4>
            <p class="text">${treatment.procedure}</p>
        </section>

        <section>
            <h4 class="section-title">Benefits</h4>
            <ul class="list">
${benefitsHTML}
            </ul>
        </section>

        <section>
            <h4 class="section-title">Cost</h4>
            <p class="text">${treatment.cost}</p>
        </section>

        <section>
            <h4 class="section-title">FAQs</h4>
${faqsHTML}
        </section>
    </div>
</body>
</html>`;

    setCodeOutput(code);
  };

  return (
    <div style={{ padding: "20px", fontFamily: "Arial, sans-serif" }}>
      <h1 style={{ textAlign: "center", marginBottom: "30px" }}>Treatment Page Creator</h1>

      <div style={{ display: "flex", gap: "20px", minHeight: "100vh" }}>
        {/* Form */}
        <div style={{ flex: 1, marginRight: "20px" }}>
          <div style={{ border: "1px solid #ccc", padding: "20px", borderRadius: "8px", backgroundColor: "#fff" }}>
            <h2 style={{ marginBottom: "20px" }}>Input Treatment Details</h2>

            <div style={{ marginBottom: "15px" }}>
              <label><strong>Title:</strong></label>
              <input
                style={{ width: "100%", padding: "10px", borderRadius: "4px", border: "1px solid #ddd", fontSize: "16px", boxSizing: "border-box", marginTop: "5px" }}
                placeholder="e.g., Root Canal Treatment"
                value={title}
                onChange={(e) => setTitle(e.target.value)}
              />
            </div>

            <div style={{ marginBottom: "15px" }}>
              <label><strong>Intro:</strong></label>
              <textarea
                style={{ width: "100%", padding: "10px", borderRadius: "4px", border: "1px solid #ddd", fontSize: "14px", boxSizing: "border-box", marginTop: "5px" }}
                rows="3"
                placeholder="Brief introduction..."
                value={intro}
                onChange={(e) => setIntro(e.target.value)}
              />
            </div>

            <div style={{ marginBottom: "15px" }}>
              <label><strong>Procedure:</strong></label>
              <textarea
                style={{ width: "100%", padding: "10px", borderRadius: "4px", border: "1px solid #ddd", fontSize: "14px", boxSizing: "border-box", marginTop: "5px" }}
                rows="3"
                placeholder="Describe the procedure..."
                value={procedure}
                onChange={(e) => setProcedure(e.target.value)}
              />
            </div>

            <div style={{ marginBottom: "15px" }}>
              <label><strong>Benefits (one per line):</strong></label>
              <textarea
                style={{ width: "100%", padding: "10px", borderRadius: "4px", border: "1px solid #ddd", fontSize: "14px", boxSizing: "border-box", marginTop: "5px" }}
                rows="4"
                placeholder="Benefit 1&#10;Benefit 2&#10;Benefit 3"
                value={benefits}
                onChange={(e) => setBenefits(e.target.value)}
              />
            </div>

            <div style={{ marginBottom: "15px" }}>
              <label><strong>Cost:</strong></label>
              <input
                style={{ width: "100%", padding: "10px", borderRadius: "4px", border: "1px solid #ddd", fontSize: "16px", boxSizing: "border-box", marginTop: "5px" }}
                placeholder="e.g., $300 - $600"
                value={cost}
                onChange={(e) => setCost(e.target.value)}
              />
            </div>

            <div style={{ marginBottom: "15px" }}>
              <label><strong>FAQs (Q and A separated by newline, entries separated by ---):</strong></label>
              <textarea
                style={{ width: "100%", padding: "10px", borderRadius: "4px", border: "1px solid #ddd", fontSize: "14px", boxSizing: "border-box", marginTop: "5px" }}
                rows="6"
                placeholder="Is the procedure painful?&#10;Most patients feel minimal discomfort...&#10;---&#10;How many visits?&#10;Usually 1 to 2 visits..."
                value={faqs}
                onChange={(e) => setFaqs(e.target.value)}
              />
            </div>

            <button
              style={{ padding: "12px 24px", backgroundColor: "#007bff", color: "white", border: "none", borderRadius: "4px", cursor: "pointer", fontSize: "16px", fontWeight: "bold" }}
              onClick={handlePreview}
            >
              Preview Treatment Page
            </button>
          </div>
        </div>

        {/* Preview */}
        <div style={{ flex: 1, borderLeft: "1px solid #ddd", paddingLeft: "20px" }}>
          {preview ? (
            <div style={{ border: "1px solid #ccc", padding: "20px", borderRadius: "8px", backgroundColor: "#f9f9f9" }}>
              <h2 style={{ marginBottom: "20px" }}>Preview</h2>
              <TreatmentTemplate treatment={preview} />
            </div>
          ) : (
            <p style={{ color: "#999", textAlign: "center", marginTop: "50px" }}>Preview will appear here</p>
          )}
        </div>
      </div>
    </div>
  );
}
