import { useState } from "react";

export default function TreatmentForm() {
  const [title, setTitle] = useState("");
  const [metaTitle, setMetaTitle] = useState("");
  const [metaDescription, setMetaDescription] = useState("");
  const [slug, setSlug] = useState("");
  const [content, setContent] = useState("");
  const [codeOutput, setCodeOutput] = useState("");
  const [outputFormat, setOutputFormat] = useState("html");

  const generateSlug = (text) => {
    return text
      .toLowerCase()
      .trim()
      .replace(/[^a-z0-9\s]/g, "")
      .replace(/\s+/g, "-");
  };

  const handleTitleChange = (e) => {
    const newTitle = e.target.value;
    setTitle(newTitle);
    setSlug(generateSlug(newTitle));
    if (!metaTitle) setMetaTitle(newTitle);
  };

  const handleGenerate = () => {
    const canonical = `https://yourwebsite.com/${slug}`;

    if (outputFormat === "html") {
      generateHTMLCode(canonical);
    } else if (outputFormat === "react") {
      generateReactCode(canonical);
    }
  };

  const generateHTMLCode = (canonical) => {
    const code = `<!DOCTYPE html>
<html lang="en">
<head>
    <meta charset="UTF-8">
    <meta name="viewport" content="width=device-width, initial-scale=1.0">
    <title>${metaTitle}</title>
    <meta name="description" content="${metaDescription}">
    <link rel="canonical" href="${canonical}" />
    <style>
        * {
            margin: 0;
            padding: 0;
            box-sizing: border-box;
        }
        body {
            font-family: Arial, sans-serif;
            background-color: #f9f9f9;
            line-height: 1.6;
        }
        .container {
            max-width: 1200px;
            margin: 0 auto;
            padding: 20px;
        }
        h1 {
            font-size: 32px;
            color: #333;
            margin-bottom: 20px;
        }
        p {
            font-size: 16px;
            color: #666;
            margin-bottom: 15px;
        }
        h2, h3 {
            font-size: 24px;
            color: #555;
            margin-top: 30px;
            margin-bottom: 15px;
        }
        ul {
            margin-left: 20px;
            margin-bottom: 15px;
        }
        li {
            margin-bottom: 8px;
            color: #666;
        }
    </style>
</head>
<body>
    <div class="container">
        <h1>${title}</h1>
        ${content
          .split("\n\n")
          .map((para) => {
            if (para.trim().startsWith("-")) {
              const items = para
                .split("\n")
                .map((item) => item.replace(/^-\s*/, "").trim())
                .filter((i) => i);
              return `<ul>\n${items.map((item) => `            <li>${item}</li>`).join("\n")}\n        </ul>`;
            }
            return `        <p>${para.trim()}</p>`;
          })
          .join("\n        ")}
    </div>
</body>
</html>`;

    setCodeOutput(code);
  };

  const generateReactCode = (canonical) => {
    const code = `import React from 'react';

export default function ${title.replace(/\s+/g, "")}() {
  return (
    <>
      <head>
        <title>${metaTitle}</title>
        <meta name="description" content="${metaDescription}" />
        <link rel="canonical" href="${canonical}" />
      </head>

      <div style={{ maxWidth: "1200px", margin: "0 auto", padding: "20px", fontFamily: "Arial, sans-serif" }}>
        <h1 style={{ fontSize: "32px", color: "#333", marginBottom: "20px" }}>${title}</h1>

        ${content
          .split("\n\n")
          .map((para, idx) => {
            if (para.trim().startsWith("-")) {
              const items = para
                .split("\n")
                .map((item) => item.replace(/^-\s*/, "").trim())
                .filter((i) => i);
              return `<ul key={${idx}} style={{ marginLeft: "20px", marginBottom: "15px" }}>
          {${JSON.stringify(items)}.map((item, i) => (
            <li key={i} style={{ marginBottom: "8px", color: "#666" }}>{item}</li>
          ))}
        </ul>`;
            }
            return `<p key={${idx}} style={{ fontSize: "16px", color: "#666", marginBottom: "15px" }}>${para.trim()}</p>`;
          })
          .join("\n        ")}
      </div>
    </>
  );
}`;

    setCodeOutput(code);
  };

  return (
    <div style={{ padding: "20px", fontFamily: "Arial, sans-serif" }}>
      <h1 style={{ textAlign: "center", marginBottom: "30px" }}>Treatment Page Code Generator</h1>

      <div style={{ display: "flex", gap: "20px", minHeight: "100vh" }}>
        {/* Form */}
        <div style={{ flex: 1, marginRight: "20px" }}>
          <div style={{ border: "1px solid #ccc", padding: "20px", borderRadius: "8px", backgroundColor: "#fff" }}>
            <h2 style={{ marginBottom: "20px" }}>Input Treatment Details</h2>

            <div style={{ marginBottom: "15px" }}>
              <label><strong>Title:</strong></label>
              <input
                style={{ width: "100%", padding: "10px", borderRadius: "4px", border: "1px solid #ddd", fontSize: "16px", boxSizing: "border-box", marginTop: "5px" }}
                placeholder="e.g., Root Canal Treatment"
                value={title}
                onChange={(e) => setTitle(e.target.value)}
              />
            </div>

            <div style={{ marginBottom: "15px" }}>
              <label><strong>Intro:</strong></label>
              <textarea
                style={{ width: "100%", padding: "10px", borderRadius: "4px", border: "1px solid #ddd", fontSize: "14px", boxSizing: "border-box", marginTop: "5px" }}
                rows="3"
                placeholder="Brief introduction..."
                value={intro}
                onChange={(e) => setIntro(e.target.value)}
              />
            </div>

            <div style={{ marginBottom: "15px" }}>
              <label><strong>Procedure:</strong></label>
              <textarea
                style={{ width: "100%", padding: "10px", borderRadius: "4px", border: "1px solid #ddd", fontSize: "14px", boxSizing: "border-box", marginTop: "5px" }}
                rows="3"
                placeholder="Describe the procedure..."
                value={procedure}
                onChange={(e) => setProcedure(e.target.value)}
              />
            </div>

            <div style={{ marginBottom: "15px" }}>
              <label><strong>Benefits (one per line):</strong></label>
              <textarea
                style={{ width: "100%", padding: "10px", borderRadius: "4px", border: "1px solid #ddd", fontSize: "14px", boxSizing: "border-box", marginTop: "5px" }}
                rows="4"
                placeholder="Benefit 1&#10;Benefit 2&#10;Benefit 3"
                value={benefits}
                onChange={(e) => setBenefits(e.target.value)}
              />
            </div>

            <div style={{ marginBottom: "15px" }}>
              <label><strong>Cost:</strong></label>
              <input
                style={{ width: "100%", padding: "10px", borderRadius: "4px", border: "1px solid #ddd", fontSize: "16px", boxSizing: "border-box", marginTop: "5px" }}
                placeholder="e.g., $300 - $600"
                value={cost}
                onChange={(e) => setCost(e.target.value)}
              />
            </div>

            <div style={{ marginBottom: "15px" }}>
              <label><strong>FAQs (Q and A separated by newline, entries separated by ---):</strong></label>
              <textarea
                style={{ width: "100%", padding: "10px", borderRadius: "4px", border: "1px solid #ddd", fontSize: "14px", boxSizing: "border-box", marginTop: "5px" }}
                rows="6"
                placeholder="Is the procedure painful?&#10;Most patients feel minimal discomfort...&#10;---&#10;How many visits?&#10;Usually 1 to 2 visits..."
                value={faqs}
                onChange={(e) => setFaqs(e.target.value)}
              />
            </div>

            <button
              style={{ padding: "12px 24px", backgroundColor: "#007bff", color: "white", border: "none", borderRadius: "4px", cursor: "pointer", fontSize: "16px", fontWeight: "bold" }}
              onClick={handlePreview}
            >
              Generate Code
            </button>
          </div>
        </div>

        {/* Code Output */}
        <div style={{ flex: 1, borderLeft: "1px solid #ddd", paddingLeft: "20px" }}>
          {codeOutput ? (
            <div style={{ border: "1px solid #ccc", padding: "20px", borderRadius: "8px", backgroundColor: "#f9f9f9" }}>
              <div style={{ marginBottom: "15px", display: "flex", gap: "10px" }}>
                <label><strong>Format:</strong></label>
                <select 
                  value={outputFormat} 
                  onChange={(e) => {
                    setOutputFormat(e.target.value);
                    generateCode(preview);
                  }}
                  style={{ padding: "5px 10px", borderRadius: "4px", border: "1px solid #ddd" }}
                >
                  <option value="react">React Component</option>
                  <option value="html">HTML Standalone</option>
                </select>
              </div>

              <div style={{ position: "relative" }}>
                <textarea
                  readOnly
                  value={codeOutput}
                  style={{
                    width: "100%",
                    height: "400px",
                    padding: "15px",
                    borderRadius: "4px",
                    border: "1px solid #ddd",
                    fontFamily: "monospace",
                    fontSize: "12px",
                    backgroundColor: "#fff",
                  }}
                />
                <button
                  onClick={() => {
                    navigator.clipboard.writeText(codeOutput);
                    alert("Code copied to clipboard!");
                  }}
                  style={{
                    position: "absolute",
                    top: "10px",
                    right: "10px",
                    padding: "8px 16px",
                    backgroundColor: "#28a745",
                    color: "white",
                    border: "none",
                    borderRadius: "4px",
                    cursor: "pointer",
                    fontSize: "14px",
                    fontWeight: "bold",
                  }}
                >
                  Copy Code
                </button>
              </div>
            </div>
          ) : (
            <p style={{ color: "#999", textAlign: "center", marginTop: "50px" }}>Code will appear here</p>
          )}
        </div>
      </div>
    </div>
  );
}
