import { useState } from "react";

export default function SitemapGenerator() {
  const [baseUrl, setBaseUrl] = useState("");
  const [urlList, setUrlList] = useState("");
  const [sitemap, setSitemap] = useState("");

  const generateSitemap = () => {
    if (!baseUrl.trim() || !urlList.trim()) {
      alert("Please enter both base URL and page list");
      return;
    }

    const urls = urlList
      .split("\n")
      .map((url) => url.trim())
      .filter((url) => url.length > 0);

    if (urls.length === 0) {
      alert("Please enter at least one URL");
      return;
    }

    let xml = '<?xml version="1.0" encoding="UTF-8"?>\n';
    xml += '<urlset xmlns="http://www.sitemaps.org/schemas/sitemap/0.9">\n';

    urls.forEach((url) => {
      const fullUrl = baseUrl.endsWith("/") ? baseUrl + url : baseUrl + "/" + url;
      xml += "  <url>\n";
      xml += `    <loc>${fullUrl}</loc>\n`;
      xml += "  </url>\n";
    });

    xml += "</urlset>";

    setSitemap(xml);
  };

  const copySitemap = () => {
    navigator.clipboard.writeText(sitemap);
    alert("Sitemap copied to clipboard!");
  };

  const downloadSitemap = () => {
    const element = document.createElement("a");
    element.setAttribute(
      "href",
      "data:text/xml;charset=utf-8," + encodeURIComponent(sitemap)
    );
    element.setAttribute("download", "sitemap.xml");
    element.style.display = "none";
    document.body.appendChild(element);
    element.click();
    document.body.removeChild(element);
  };

  return (
    <div style={{ border: "1px solid #ccc", padding: "20px", borderRadius: "8px", marginBottom: "20px", backgroundColor: "#fff" }}>
      <h2 style={{ marginBottom: "20px" }}>Sitemap Generator</h2>

      <div style={{ marginBottom: "15px" }}>
        <label><strong>Base URL:</strong></label>
        <input
          style={{ width: "100%", padding: "10px", borderRadius: "4px", border: "1px solid #ddd", fontFamily: "Arial", marginTop: "5px", fontSize: "16px", boxSizing: "border-box" }}
          placeholder="https://example.com"
          value={baseUrl}
          onChange={(e) => setBaseUrl(e.target.value)}
        />
      </div>

      <div style={{ marginBottom: "15px" }}>
        <label><strong>Page URLs (one per line):</strong></label>
        <textarea
          style={{ width: "100%", padding: "10px", borderRadius: "4px", border: "1px solid #ddd", fontFamily: "monospace", marginTop: "5px", fontSize: "14px", boxSizing: "border-box" }}
          rows="6"
          placeholder="about&#10;services&#10;contact&#10;blog/post-1"
          value={urlList}
          onChange={(e) => setUrlList(e.target.value)}
        />
      </div>

      <button
        style={{ padding: "10px 20px", backgroundColor: "#ff9800", color: "white", border: "none", borderRadius: "4px", cursor: "pointer", fontSize: "16px", marginRight: "10px", marginBottom: "10px" }}
        onClick={generateSitemap}
      >
        Generate Sitemap
      </button>

      {sitemap && (
        <div style={{ marginTop: "20px" }}>
          <label><strong>Generated Sitemap XML:</strong></label>
          <textarea
            style={{ width: "100%", padding: "10px", borderRadius: "4px", border: "1px solid #ddd", fontFamily: "monospace", marginTop: "10px", fontSize: "12px", boxSizing: "border-box", backgroundColor: "#f5f5f5" }}
            rows="8"
            value={sitemap}
            readOnly
          />

          <div style={{ marginTop: "10px" }}>
            <button
              style={{ padding: "8px 16px", backgroundColor: "#4CAF50", color: "white", border: "none", borderRadius: "4px", cursor: "pointer", fontSize: "14px", marginRight: "10px" }}
              onClick={copySitemap}
            >
              Copy to Clipboard
            </button>
            <button
              style={{ padding: "8px 16px", backgroundColor: "#2196F3", color: "white", border: "none", borderRadius: "4px", cursor: "pointer", fontSize: "14px" }}
              onClick={downloadSitemap}
            >
              Download sitemap.xml
            </button>
          </div>
        </div>
      )}
    </div>
  );
}
