import { useState } from "react";

export default function TreatmentForm() {
  const [title, setTitle] = useState("");
  const [metaTitle, setMetaTitle] = useState("");
  const [metaDescription, setMetaDescription] = useState("");
  const [slug, setSlug] = useState("");
  const [content, setContent] = useState("");
  const [codeOutput, setCodeOutput] = useState("");
  const [outputFormat, setOutputFormat] = useState("html");
  const [showView, setShowView] = useState("code"); // "code" or "preview"

  const generateSlug = (text) => {
    return text
      .toLowerCase()
      .trim()
      .replace(/[^a-z0-9\s]/g, "")
      .replace(/\s+/g, "-");
  };

  const handleTitleChange = (e) => {
    const newTitle = e.target.value;
    setTitle(newTitle);
    setSlug(generateSlug(newTitle));
    if (!metaTitle) setMetaTitle(newTitle);
  };

  const handleGenerate = () => {
    const canonical = `https://yourwebsite.com/${slug}`;

    if (outputFormat === "html") {
      generateHTMLCode(canonical);
    } else if (outputFormat === "react") {
      generateReactCode(canonical);
    }
  };

  const renderPreview = () => {
    const paragraphs = content.split("\n\n").filter((p) => p.trim());

    return (
      <div style={{ maxWidth: "800px", margin: "0 auto", padding: "30px 20px", backgroundColor: "#fff" }}>
        {title && (
          <h1 style={{ fontSize: "32px", color: "#333", marginBottom: "30px", borderBottom: "2px solid #007bff", paddingBottom: "15px" }}>
            {title}
          </h1>
        )}
        {paragraphs.map((para, idx) => {
          const trimmed = para.trim();
          if (trimmed.startsWith("-")) {
            const items = trimmed
              .split("\n")
              .map((item) => item.replace(/^-\s*/, "").trim())
              .filter((i) => i);
            return (
              <ul key={idx} style={{ marginLeft: "20px", marginBottom: "20px", listStyleType: "disc" }}>
                {items.map((item, i) => (
                  <li key={i} style={{ marginBottom: "8px", color: "#666", fontSize: "16px", lineHeight: "1.6" }}>
                    {item}
                  </li>
                ))}
              </ul>
            );
          }
          return (
            <p key={idx} style={{ fontSize: "16px", color: "#666", marginBottom: "15px", lineHeight: "1.8" }}>
              {trimmed}
            </p>
          );
        })}
      </div>
    );
  };

  const generateHTMLCode = (canonical) => {
    const code = `<!DOCTYPE html>
<html lang="en">
<head>
    <meta charset="UTF-8">
    <meta name="viewport" content="width=device-width, initial-scale=1.0">
    <title>${metaTitle}</title>
